import React, { useState, useEffect } from "react";
import Logo from "../../img/login.png";
import { FcGoogle } from "react-icons/fc";
import "./Login.css";
import { Link } from "react-router-dom";

const Login = () => {
  const initialState = {
    fullname: "",
    email: "",
    password: "",
    adress: "",
    city: "",
    birthday: "",
    gender: "",
    competence: "",
    experience: "",
    verify: "",
  };
  useEffect(() => {
    first;

    return () => {
      second;
    };
  }, [third]);

  const [signUp, setSignUp] = useState(false);
  const [isExpert, setIsExpert] = useState(false);
  const [isCompany, setIsCompany] = useState(false);
  const [data, setData] = useState(initialState);
  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };
  return (
    <section>
      <div className="imgBx">
        <img src={Logo} alt="logo" />
      </div>
      <div className="contentBx">
        <div className="formBx">
          <h1>WORKHUB</h1>
          <p>Discover new opportunities everyday</p>

          <form>
            {signUp && (
              <select className="selectForm">
                <option value="User">User</option>
                <option value="Expert">Expert</option>
                <option value="Company">Company</option>
              </select>
            )}
            {signUp && (
              <div className="inputBx">
                <span>FullName</span>
                <input
                  required
                  placeholder="Full name"
                  type="text"
                  name="fullname"
                  value={data.fullname}
                  onChange={handleChange}
                />
              </div>
            )}
            <div className="inputBx">
              <span>Email</span>
              <input
                required
                placeholder="Full name"
                type="text"
                name="email"
                value={data.fullname}
                onChange={handleChange}
              />
            </div>
            <div className="inputBx">
              <span>Password</span>
              <input type="password" name="password" />
            </div>
            {signUp && (
              <div className="inputBx">
                <span>َAdress</span>
                <input type="password" name="adress" />
              </div>
            )}
            {signUp && (
              <div className="inputBx">
                <span>City</span>
                <input type="text" name="city" />
              </div>
            )}

            {isExpert && (
              <div>
                <div className="inputBx">
                  <span>Birthday</span>
                  <input type="date" name="birthday" />
                </div>
                <div className="radio">
                  <span>Gender</span>
                  <input type="radio" name="gender" />
                  <label>Homme</label>
                  <input type="radio" name="gender" />
                  <label>Femme</label>
                </div>
                <div className="inputBx">
                  <span>Competence</span>
                  <input type="text" name="comptence" />
                </div>
                <div className="inputBx">
                  <span>Expercience</span>
                  <input type="text" name="experience" />
                </div>
                <div className="inputBx">
                  <span>Verify</span>
                  <input type="file" name="verify" />
                </div>
              </div>
            )}

            {isCompany && (
              <div>
                <div className="inputBx">
                  <span>Web Site</span>
                  <input type="text" name="" />
                </div>
                <div className="inputBx">
                  <span>Verify</span>
                  <input type="file" name="" />
                </div>
              </div>
            )}

            <div className="remember">
              <label>
                <input type="checkbox" name="" />
                Remember me
              </label>
              <p>
                <Link to="/forget-password">Forget Password?</Link>
              </p>
            </div>
            <div className="inputBx">
              <input type="submit" value="Sign in" name="" />
            </div>
            <div className="inputBx">
              <p
                style={{ cursor: "pointer", textDecoration: "underline" }}
                onClick={() => setSignUp((prev) => !prev)}
              >
                Don't have an account? <span>Sign up</span>
              </p>
            </div>
          </form>
          <hr />
          <button type="submit" className="GoogleBtn">
            <FcGoogle /> &nbsp; Login with google
          </button>
        </div>
      </div>
    </section>
  );
};

export default Login;

