import React, { useState } from "react";

import Register from "./auth/Register";
import Login from "./auth/Login";
import loginImg from "../assets/Desktop - 2.png";
import "./home.css";

const Home = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const handleRoutes = () => {
    setIsSignUp(true);
  };
  return (
    <MDBContainer className="my-5">
      <MDBCard>
        <MDBRow className="g-0 align-items-center justify-content-center">
          <MDBCol md="4">
            <MDBCardImage
              src={loginImg}
              alt="login form"
              className="rounded-start w-100 "
            />
          </MDBCol>

          <MDBCol md="5">
            <MDBCardBody className="mx-5 d-grid grid-row align-items-center">
              <div className="d-flex flex-row ">
                <MDBIcon
                  fas
                  icon="cubes fa-3x me-3"
                  style={{ color: "#ff6219" }}
                />
                <span className="h1 fw-bold mb-0 text-center">WORKHUB</span>
              </div>

              <h5
                className="fw-normal my-4 pb-3"
                style={{ letterSpacing: "1px" }}
              >
                Discover new opportunities everyday
              </h5>
              <div className="d-lg-flex flex-column justify-content-center">
                {isSignUp ? <Register /> : <Login signup={handleRoutes} />}
              </div>
            </MDBCardBody>
          </MDBCol>
        </MDBRow>
      </MDBCard>
    </MDBContainer>
  );
};

export default Home;
